import React, {useEffect, useState} from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";
import Triumph_logo from '../images/'
import {useParams} from 'react-router-dom'
import {fetchOneDevice} from "../api/deviceAPI";

const DevicePage = () => {
    const [device, setDevice] = useState({info: []})
    const {id} = useParams()
    useEffect(() => {
        fetchOneDevice(id).then(data => setDevice(data))
    }, [])

    return (
        <Container className="mt-4">

            <Row>
                <Col md={4}>
                    <Image width={300} height={300} src={process.env.REACT_APP_API_URL + device.img}
                    style={{border: '3px solid gray'}}></Image>
                </Col>
                <Col md={5}>
                    <Row className="d-flex flex-column  first baseline">
                        <h2>{device.name}</h2>

                    </Row>
                </Col>
                <Col md={5}>
                    <Card
                        className="d-flex flex-column align-items-center justify-content-around"
                        style={{width: 300, height: 75, fontSize: 24, border: '3px solid gray'}}
                    >
                        <h3> {device.price} руб.</h3>

                    </Card>
                </Col>
            </Row>
            <Row className="d-flex flex-column m-3">
                <h1>Характеристики</h1>
                {device.info.map((info, index) =>
                    <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10}}>
                        {info.title}: {info.description}
                    </Row>
                )}
            </Row>
        </Container>
    );
};

export default DevicePage;
